import "./App.css";

import React, { version, useState } from "react";

import Button from "@mui/material/Button";

import { FormControl, InputLabel, Select, MenuItem } from "@mui/material";

function App() {
  const [selectedDevice, setSelectedDeivce] = useState(0);

  var noop = function () {};
  navigator.getUserMedia = (navigator.getUserMedia || navigator.webkitGetUserMedia || navigator.mozGetUserMedia || navigator.msGetUserMedia);

    function hasPermission(permissionName, descriptor) {
    try {
    navigator.permissions.query(Object.assign({name: permissionName}, descriptor))
      .then(function (permission) {
        console.log(`${permissionName}'s Permission state: ${permission.state}`)
        if (permission.state == 'prompt'){
          return false;

        }
        if (permission.state =='denied'){
          return true;
        }
        if (permission.state == 'granted'){
          return true;

        }
        // document.getElementById(permissionName + '-status').innerHTML = permission.state;
        // permission.addEventListener('change', function (e) {
        //   document.getElementById(permissionName + '-status').innerHTML = permission.state;
        //   // handleChange(permissionName, permission.state);
        // });
      });
    } catch (e) {
    }
  }
  const requestUserMediaPermission = async () => {
    checkPermission('camera');
    const stream = await navigator.mediaDevices.getUserMedia({ video: true }, noop, noop);
    stream.getTracks().forEach(function(track) {
      track.stop();
    });
  }
  const  getHardwareDevices = async () => {

    let counter = 0;
    const deviceIds =[];
    const deviceNames = [];

    await requestUserMediaPermission();

    navigator.mediaDevices.enumerateDevices().then(function (devices) {
      devices.forEach(function (device) {
        console.log(
          device.kind + ": " + device.label + " id = " + device.deviceId
        );

        if (device.kind == "videoinput") {
          //add a select to the camera dropdown list
          //  var option = document.createElement("option");
          console.log(device);
          deviceIds[counter] = device.deviceId;
          deviceNames[counter] = device.label;
          counter++;
        }
      });
    });
  };

  const titleDiv = () => {
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  const deviceMenu = () => {
    getHardwareDevices();
    return (
      <div className="deviceMenuContainer">
        <div className="deviceMenuDropdown">
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">SELECT DEVICE</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={selectedDevice}
              label="selected device"
              onChange={(event) => {
                console.log(`new Value: ${event.target.value}`);
                setSelectedDeivce(event.target.value);
              }}
            >
              <MenuItem value={0}>device 1</MenuItem>
              <MenuItem value={1}>device 2</MenuItem>
              <MenuItem value={2}>device 3</MenuItem>
            </Select>
          </FormControl>
        </div>
        <div className="selectedDevice">
          <p>Selected Device</p>
          <div> device 2</div>
        </div>
      </div>
    );
  };

  const controlsBar = () => {
    return (
      <>
        <div className="controlsBar">
          <div>
            <Button variant="contained" size="small" color="secondary">
              Start Recording
            </Button>
          </div>
        </div>
      </>
    );
  };
  return (
    <div className="App">
      {titleDiv()}
      {deviceMenu()}
      {controlsBar()}
    </div>
  );
}

export default App;
