import React, { useEffect } from "react";

function DeviceMenu() {

  var noop = function () {
    navigator.getUserMedia =
      navigator.getUserMedia ||
      navigator.webkitGetUserMedia ||
      navigator.mozGetUserMedia ||
      navigator.msGetUserMedia;
  };

  async function checkCameraPermisson(permissionName, descriptor) {
    let promptCameraPermission ;
    try {
      let permission = await navigator.permissions
        .query(Object.assign({ name: permissionName }, descriptor));

        console.log(
          `${permissionName}'s Permission state: ${permission.state}`
        );
        if (permission.state == "prompt") {
          console.log('should... try to prompt camera permission!!!!!');
           promptCameraPermission = true;
          return promptCameraPermission
        } else {
          console.log('shouldnt try to prompt camera permission!!!!!');
          promptCameraPermission =  false;
          return promptCameraPermission
        }
  } catch(e) {
    console.log(
      `${permissionName}'s Permission error: ${e}`
    );
  }
  return promptCameraPermission;

}

  const requestUserMediaPermission = async () => {

    const stream = await navigator.mediaDevices.getUserMedia(
      { video: true },
      noop,
      function(err){
        console.log('---');
        console.log('# Inside Error Callback');
        console.log(err);
      }
    );
    stream.getTracks().forEach(function (track) {
      track.stop();
    });

  };

  const getHardwareDevices = async () => {
    const tempDeviceList = [];

    let promptCameraPermission = await checkCameraPermisson("camera")
    console.log(`from getHardwareDevice()... shouldPromptCameraPermission:${promptCameraPermission}`)

    if (promptCameraPermission) {
      console.log("im gonna ask for permission now.. ");
       await requestUserMediaPermission();
    }



    navigator.mediaDevices.enumerateDevices().then(function (devices) {
      devices.forEach(function (device) {

        if (device.kind == "videoinput") {
          tempDeviceList.push(device);

        }
      });
      setDevicesList(tempDeviceList);

    });
  };

  const TitleDiv = () => {
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  useEffect(() => {
    // You need to restrict it at some point
    // This is just dummy code and should be replaced by actual

    if (devicesList.length > 0) return;
    getHardwareDevices();
  }, [devicesList]);
  return (
    <div className="deviceMenuContainer">
      <div className="deviceMenuDropdown">
        <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">SELECT DEVICE</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={selectedDevice}
            label="selected device"
            onChange={(event) => {
              setSelectedDevice(event.target.value);
            }}
          >
            {devicesList.map((device, i) => (
              <MenuItem value={device.label} key={i}>
                {" "}
                {device.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </div>
      <div className="selectedDevice">
        <p>Selected Device</p>
        <div> {devicesList.length > 0 ? selectedDevice : "NULL"}</div>
      </div>
    </div>
  );
}

export default DeviceMenu;
