  }, [devicesList]);
  return (
    <div className="deviceMenuContainer">
      <div className="deviceMenuDropdown">
        <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">SELECT DEVICE</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={selectedDevice}
            label="selected device"
            onChange={(event) => {
              setSelectedDevice(event.target.value);
            }}
          >
            {devicesList.map((device, i) => (
              <MenuItem value={device.label} key={i}>
                {" "}
                {device.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </div>
      <div className="selectedDevice">
        <p>Selected Device</p>
        <div> {devicesList.length > 0 ? selectedDevice : "NULL"}</div>
      </div>
    </div>
  );
}

export default DeviceMenu;
