import "./App.css";

import React, { version, useState, useEffect } from "react";

import Button from "@mui/material/Button";

import { FormControl, InputLabel, Select, MenuItem } from "@mui/material";

function App() {
  const [selectedDevice, setSelectedDeivce] = useState(0);
  const [devicesList, setDevicesList] = useState([]);
  const [shouldPromptCameraPermisson, setShouldPromptCameraPermission] = useState(false);



  var noop = function () {navigator.getUserMedia = (navigator.getUserMedia || navigator.webkitGetUserMedia || navigator.mozGetUserMedia || navigator.msGetUserMedia);};


    async function  shouldPromptPermisson(permissionName, descriptor) {
    try {
    navigator.permissions.query(Object.assign({name: permissionName}, descriptor))
      .then(function (permission) {
        console.log(`${permissionName}'s Permission state: ${permission.state}`)
        if (permission.state == 'prompt'){

          return true;

        } else{
          return false;
        }
      });
    } catch (e) {
    }
  }
  const requestUserMediaPermission = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true }, noop, noop);
    stream.getTracks().forEach(function(track) {
      track.stop();
    });
  }
  const  getHardwareDevices = async  () => {
    let counter = 0;
    const deviceMap = new Map([])

    setShouldPromptCameraPermission( await shouldPromptPermisson('camera'));
    if ( shouldPromptCameraPermisson){
        console.log('im gonna ask for permission now.. ')
      await requestUserMediaPermission();
    }

    navigator.mediaDevices.enumerateDevices().then(function (devices) {
      devices.forEach(function (device) {
        // console.log(
        //   device.kind + ": " + device.label + " id = " + device.deviceId
        // );

        if (device.kind == "videoinput") {

          deviceMap.set(device.deviceId, device.label);
          // counter++;


        }
        // console.log(deviceDict[deviceIds[0]]);
        console.log(deviceMap);
      });
    }
    );
    return deviceMap;
  };

  const TitleDiv = () => {
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  const DeviceMenu =   () => {

    useEffect(() => {
      // You need to restrict it at some point
      // This is just dummy code and should be replaced by actual

    setDevicesList(getHardwareDevices()); 
    }, [])

    return (
      <div className="deviceMenuContainer">
        <div className="deviceMenuDropdown">
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">SELECT DEVICE</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={selectedDevice}
              label="selected device"
              onChange={(event) => {
                setSelectedDeivce(event.target.value);
              }}
            >
              {/* {jsonData.experiences.map((experience, i) => (
            <>{experienceDiv(experience.id)}</>
            ))} */}
            {/* {devicesList.map( (device, i) => (<>

              <MenuItem value={device.deviceId}> {device.label}</MenuItem>
            </>)) 
            } */}
              <MenuItem value={0}>device 1</MenuItem>
              <MenuItem value={1}>device 2</MenuItem>
              <MenuItem value={2}>device 3</MenuItem>
            </Select>
          </FormControl>
        </div>
        <div className="selectedDevice">
          <p>Selected Device</p>
          <div> device 2</div>
        </div>
      </div>
    );
  };

  const controlsBar = () => {
    return (
      <>
        <div className="controlsBar">
          <div>
            <Button variant="contained" size="small" color="secondary">
              Start Recording
            </Button>
          </div>
        </div>
      </>
    );
  };
  return  (
    <div className="App">
      {TitleDiv()}
      {DeviceMenu()}
      {controlsBar()}
    </div>
  );
}

export default App;
