import "./App.css";

import React, { version, useState, useEffect } from "react";

import Button from "@mui/material/Button";


import DeviceMenu from './components/DeviceMenu'
function App() {

  const [selectedDevice, setSelectedDevice] = useState('');

  const TitleDiv = () => {
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  const controlsBar = () => {
    return (
      <>
        <div className="controlsBar">
          <div>
            <Button variant="contained" size="small" color="secondary">
              Start Recording
            </Button>
          </div>
        </div>
      </>
    );
  };

  const onDeviceSelected = (selectedDevice) => {
    console.log(`[FROM APP.JS] Selected device label: ${selectedDevice.label}`);
    setSelectedDevice(selectedDevice);
    console.log(`[FROM APP.JS] Selected device id : ${selectedDevice.deviceId}`);

  }

  return (
    <div className="App">
      {TitleDiv()}
      <DeviceMenu onDeviceSelected = {onDeviceSelected}/>
      {/* {DeviceMenu(onDeviceSelected)} */}
      {controlsBar()}
    </div>
  );
}

export default App;
