import "./App.css";
import MuiThemeProvider from "@material-ui/core/styles/MuiThemeProvider";
import { version } from "react";
// import { Button } from '@material-ui/core';
import Button from "@mui/material/Button";
import DropDownMenu from "@mui/material/MenuItem";
// import MenuItem from "@material-ui/core/MenuItem";
// import { MenuList } from 'material-ui/DropDownMenu';
import { FormControl, InputLabel, Select,  MenuItem } from "@mui/material";

function App() {
  const titleDiv = () => {
    console.log(version);
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  const deviceMenu = () => {
    return (
      <>
        <div className="deviceMenuDropdown">
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">SELECT DEVICE</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={0}
              label="SELECT DEVICES"
              onChange={() => {}}
            >
              <MenuItem value={1}>device 1</MenuItem>
              <MenuItem value={2}>device 2</MenuItem>
              <MenuItem value={3}>device 3</MenuItem>

          <h2>Selected Device</h2>
            </Select>
          </FormControl>
        </div>


      </>
    );
  };

  const controlsBar = () => {
    return (
      <>
        <div className="controlsBar">
          <div>
            <Button variant="contained" size="small" color="secondary">
              Start Recording
            </Button>
          </div>

        </div>
      </>
    );
  };
  return (
    <div className="App">
      {titleDiv()}
      {deviceMenu()}
      {controlsBar()}
    </div>
  );
}

export default App;
