import "./App.css";

import React, { version, useState } from "react";

import Button from "@mui/material/Button";

import DeviceMenu from "./components/DeviceMenu";
import Camera from "./components/Camera";
function App() {
  const [selectedDevice, setSelectedDevice] = useState(null);

  const TitleDiv = () => {
    return (
      <div className="titleDiv">
        <h1> Camera API WebApp</h1>
      </div>
    );
  };

  const controlsBar = () => {
    return (
      <>
        <div className="controlsBar">
          <div>
            <Button variant="contained" size="small" color="secondary">
              Start Recording
            </Button>
          </div>
        </div>
      </>
    );
  };

  const onDeviceSelected = (selectedDevice) => {
    setSelectedDevice(selectedDevice);
  };

  return (
    <div className="App">
      {TitleDiv()}
      <DeviceMenu onDeviceSelected={onDeviceSelected} />
      {/* {DeviceMenu(onDeviceSelected)} */}
      {controlsBar()}
      {selectedDevice && <Camera deviceId={selectedDevice} />}

    </div>
  );
}

export default App;
