import React, { useRef, useState } from "react";

const VideoCanvas = (deviceId) => {
  const [mediaStream, setMediaStream] = useState(null);
  const videoRef = useRef();
  navigator.getUserMedia =
    navigator.mediaDevices.getUserMedia ||
    navigator.mediaDevices.mozGetUserMedia ||
    navigator.mediaDevices.msGetUserMedia ||
    navigator.mediaDevices.webkitGetUserMedia;

  let cameraW = 1280;
  let cameraH = 720;
  let cameraFR = 25;
  var camera1Options = {
    audio: false,
    video: {
      deviceId: deviceId,
      width: { min: 100, ideal: cameraW, max: 1920 },
      height: { min: 100, ideal: cameraH, max: 1080 },
      frameRate: { ideal: cameraFR },
    },
  };
  const userUserMedia = () => {
    useEffect(() => {
      async function enableStream() {
        try {
          const stream = await navigator.mediaDevices.getUserMedia(
            camera1Options
          );
          setMediaStream(stream);
        } catch (err) {
          console.log(`error while enabling stream..   ${err}`);
        }
      }
    });

    if (!mediaStream) {
      enableStream();
    } else {
      return function cleanup() {
        mediaStream.getTracks().forEach((track) => {
          track.stop();
        });
      };
    }

    return mediaStream;
  };

  if (mediaStream && videoRef.current && !videoRef.current.srcObject) {
    videoRef.current.srcObject = mediaStream;
  }

  function handleCanPlay() {
    videoRef.current.play();
  }

  return (
    <>
      <div>VideoCanvas</div>
      <video id="video1"></video>
      <div>{`Showing Device: ${deviceId.deviceId}`}</div>
      <video ref={videoRef} onCanPlay={handleCanPlay} autoPlay playsInline muted />
    </>
  );
};

export default VideoCanvas;
