  }

  return (
    <>
      <div>Camera</div>
      {/* <video id="video1"></video> */}
      {/* <div>{`Showing Device: ${deviceId.deviceId}`}</div> */}
      <video ref={videoRef}  onCanPlay={handleCanPlay} autoPlay playsInline muted type="video/mp4" />
    </>
  );
};

export default Camera;
